class MenuDropdown {
    constructor() {
      this.dropdownItems = document.querySelectorAll('.menu__item');
      this.initialize();
    }
  
    initialize() {
      this.dropdownItems.forEach((item) => {
        const dropdown = item.querySelector('.menu__dropdown');
        const link = item.querySelector('.menu__link');
  
        if (dropdown) {
          item.classList.add('is-dropdown');
          link.removeAttribute('href');
        }
      });
    }
}

class MobileMenu {
    constructor() {
      this.mobileBtn = document.querySelector('.header__mobile-btn');
      this.body = document.querySelector('body');
      this.initialize();
    }
  
    initialize() {
      this.mobileBtn.addEventListener('click', () => {
        this.mobileBtn.classList.toggle('is-active');
        this.body.classList.toggle('is-menu');
      });
    }
}

class FAQCard {
    constructor(cardElement) {
      this.cardElement = cardElement;
      this.headerElement = cardElement.querySelector('.faq-card__header');
      this.bodyElement = cardElement.querySelector('.faq-card__body');
      this.initialize();
    }
  
    initialize() {
      this.headerElement.addEventListener('click', () => {
        this.cardElement.classList.toggle('is-opened');
        this.closeOtherCards();
      });
    }
  
    closeOtherCards() {
      const allCards = document.querySelectorAll('.js-faq-card');
      allCards.forEach((card) => {
        if (card !== this.cardElement) {
          card.classList.remove('is-opened');
        }
      });
    }
}
  
  const faqCards = document.querySelectorAll('.js-faq-card');

  faqCards.forEach((card) => {
    new FAQCard(card);
  });
  
  
const menu = new MenuDropdown();
const mobileMenu = new MobileMenu();
  